// Set stores a key-value pair in the database.
// ‚úÖ CORRIGIDO: A tabela TEM created_at e updated_at - usar DEFAULT NOW() do banco
// üö´ VALIDA√á√ÉO: Previne uso indevido de KV Store para dados cr√≠ticos
export const set = async (key: string, value: any): Promise<void> => {
  // ‚úÖ Validar uso de KV Store (previne dados cr√≠ticos em KV Store)
  // üö´ REGRA: KV Store APENAS para cache tempor√°rio (veja REGRA_KV_STORE_VS_SQL.md)
  const criticalPatterns = [
    /^user:/i,
    /^session:/i,
    /^conversation:/i,
    /^message:/i,
    /^reservation:/i,
    /^property:/i,
    /^listing:/i,
    /^organization:/i,
    /^org:/i,
    /^config:/i,
    /^channel_config:/i,
    /^acc:/i,
    /^res:/i,
    /^guest:/i,
    /^booking:/i,
    /chat:conversation:/i,
    /chat:message:/i,
  ];
  
  const isCritical = criticalPatterns.some(pattern => pattern.test(key));
  if (isCritical) {
    const allowedPrefixes = ['cache:', 'process:', 'temp:', 'lock:', 'queue:'];
    const hasAllowedPrefix = allowedPrefixes.some(prefix => key.startsWith(prefix));
    
    if (!hasAllowedPrefix) {
      throw new Error(
        `‚ùå PROIBIDO: N√£o use KV Store para dados cr√≠ticos!\n` +
        `   Key: ${key}\n` +
        `   REGRA: KV Store APENAS para cache tempor√°rio (TTL < 24h)\n` +
        `   SOLU√á√ÉO: Use tabela SQL apropriada (users, conversations, messages, etc)\n` +
        `   üìö Veja: REGRA_KV_STORE_VS_SQL.md\n` +
        `   ‚úÖ Prefixos permitidos: cache:*, process:*, temp:*, lock:*, queue:*`
      );
    }
  }
  
  const supabase = client()
  // ‚úÖ CORRIGIDO: Especificar updated_at explicitamente para evitar erro de trigger
  // Para INSERT, created_at e updated_at usam DEFAULT NOW()
  // Para UPDATE, updated_at √© atualizado pela trigger, mas especificamos aqui tamb√©m para garantir
  const { error } = await supabase.from("kv_store_67caf26a").upsert({
    key,
    value,
    updated_at: new Date().toISOString()
    // created_at n√£o especificamos - deixa o banco usar DEFAULT NOW() apenas no INSERT
  }, {
    onConflict: 'key'
  });
  if (error) {
    throw new Error(error.message);
  }
};

